import axios from 'axios';
import React, { useRef, useState } from 'react';
import {
  Navbar,
  NavbarBrand,
  Button,
  Modal, 
  ModalHeader, 
  ModalBody, 
  ModalFooter, 
  Input
} from 'reactstrap';

function Header() {
  const [modal, setModal] = useState(false);
  const [title, setTitle] = useState("");
  const [time, setTime] = useState("");
  const [status, setStatus] = useState("");
  const counter = useRef(0);

  const onTitleChange = e => setTitle(e.target.value);
  const onTimeChange = e => setTime(e.target.value);
  const onStatusChange = e => setStatus(e.target.value);

  const toggleModal = () => setModal(!modal);

  const handleSubmit = (e) => {
    // toggleModal()
    // e.preventDefault()
    // counter.current = counter.current + 1;

    // axios.post('http://192.168.33.119:7000/api/posts', {title, time, status})
    // .then((response)=>{
    //     console.log(response)
    // })
    // .catch((error) => {
    //     console.log(error)
    // })
  }
  return (
    <div>
      <Navbar>
        <NavbarBrand href="/">Zoftify</NavbarBrand>
        <Button onClick={toggleModal}>Add Post</Button>

      <Modal  isOpen={modal} toggle={toggleModal}>
        <ModalHeader toggle={toggleModal}>Add Post</ModalHeader>

        <ModalBody>
          <Input 
            value={title}
            onChange={onTitleChange} 
            placeholder="Add Title"
            className="mb-2"
          />
          <Input
            value={time}
            onChange={onTimeChange}
            placeholder="Add Time"
            className="mb-2"
          />
          <Input 
            value={status}
            onChange={onStatusChange}
            placeholder="Add Status"

          />
        </ModalBody>

        <ModalFooter>
          <Button color="primary" onSubmit={handleSubmit}>
            Submit
          </Button>
          <Button color="secondary" onClick={toggleModal}>
            Cancel
          </Button>
        </ModalFooter>
      </Modal>
      </Navbar>
    </div>
  );
}

export default Header;
